import 'dart:convert';
import 'package:flutter/material.dart';
import 'package:get/get.dart';
import 'package:openvpn_flutter/openvpn_flutter.dart';
import 'package:vpn/Router/route.dart';
import 'package:vpn/controller/update_controller.dart';
import 'package:vpn/controller/vpn_controller.dart';
import 'package:vpn/model/vpn.dart';

class HomeUI extends StatefulWidget {
  const HomeUI({Key? key}) : super(key: key);

  @override
  State<HomeUI> createState() => _HomeUIState();
}

class _HomeUIState extends State<HomeUI> {
  UpdateController controller = Get.put(UpdateController());

  late OpenVPN engine;
  VpnStatus? status;
  VPNStage? stage;
  bool _granted = false;

  @override
  void initState() {
    engine = OpenVPN(
      onVpnStatusChanged: (data) {
        setState(() {
          status = data;
        });
      },
      onVpnStageChanged: (data, raw) {
        setState(() {
          stage = data;
        });
      },
    );
    engine.initialize(
        groupIdentifier: "group.com.laskarmedia.vpn",
        providerBundleIdentifier:
            "id.laskarmedia.openvpnFlutterExample.VPNExtension",
        localizedDescription: "VPN by OvO God");
    super.initState();
    controller.initRemoteConfig();
  }

  Future<void> initPlatformState({required Vpn vpn}) async {
    engine.connect(
        utf8.fuse(base64).decode(vpn.config!), vpn.serverName ?? "OvO Server",
        username: vpn.username, password: vpn.password);
    if (!mounted) return;
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Row(
          mainAxisAlignment: MainAxisAlignment.spaceBetween,
          children: const [
            Text(
              'OvO VPN',
              style: TextStyle(color: Colors.black),
            ),
            // GestureDetector(
            //   onTap: () {
            //     // FirebaseFirestore.instance.collection("vpnServer").add({
            //     //   "server_name": "Canada (Montreal)",
            //     //   "cod": "CN",
            //     //   "config": "",
            //     //   "username":"ovo",
            //     //   "password":"12345"
            //     // }).then((value) {
            //     //   print(value.id);
            //     // });
            //   },
            //   child: const Icon(
            //     Icons.settings_sharp,
            //     color: Colors.black,
            //   ),
            // ),
          ],
        ),
      ),
      body: Column(children: [
        //
        GetBuilder<VpnController>(
            init: VpnController(),
            builder: (controller) {
              return Card(
                child: SizedBox(
                  height: 170,
                  child: Stack(
                    children: [
                      Positioned(
                        left: 15,
                        top: 15,
                        child: Container(
                          padding: const EdgeInsets.all(5.0),
                          height: 80,
                          child: Image.asset(
                            (stage.toString() == VPNStage.connected.toString())
                                ? "assets/icon/vpn.png"
                                : "assets/icon/vpn_off.png",
                          ),
                        ),
                      ),
                      Positioned(
                        left: 10,
                        bottom: 10,
                        child: Column(
                          mainAxisAlignment: MainAxisAlignment.center,
                          crossAxisAlignment: CrossAxisAlignment.start,
                          children: [
                            Row(
                              children: [
                                const Icon(
                                  Icons.north_sharp,
                                  size: 15,
                                ),
                                Text(
                                  "Up      : ${status!.byteOut.toString()} bytes",
                                  style: const TextStyle(
                                      fontWeight: FontWeight.w500,
                                      fontSize: 15),
                                ),
                              ],
                            ),
                            const SizedBox(
                              height: 8,
                            ),
                            Row(
                              children: [
                                const Icon(
                                  Icons.south_sharp,
                                  size: 15,
                                ),
                                Text(
                                  "Down : ${status!.byteIn.toString()} bytes",
                                  style: const TextStyle(
                                      fontWeight: FontWeight.w500,
                                      fontSize: 15),
                                ),
                              ],
                            ),
                            const SizedBox(
                              height: 5,
                            ),
                          ],
                        ),
                      ),
                      Positioned(
                        right: 8,
                        top: 10,
                        bottom: 5,
                        child: Column(
                          mainAxisAlignment: MainAxisAlignment.end,
                          crossAxisAlignment: CrossAxisAlignment.start,
                          mainAxisSize: MainAxisSize.min,
                          children: [
                            controller.haveVpn
                                ? Column(
                                    children: [
                                      const SizedBox(
                                        height: 15,
                                      ),
                                      Padding(
                                        padding: const EdgeInsets.only(
                                            left: 10, right: 5),
                                        child: Column(
                                          mainAxisAlignment:
                                              MainAxisAlignment.start,
                                          crossAxisAlignment:
                                              CrossAxisAlignment.start,
                                          children: [
                                            Image.asset(
                                                "assets/flag/${controller.vpn!.cod ?? "US"}.png",
                                                height: 35),
                                            const SizedBox(
                                              height: 10,
                                            ),
                                            Text(
                                                (stage.toString() ==
                                                            VPNStage
                                                                .disconnected
                                                                .toString() ||
                                                        stage.toString() ==
                                                            "null")
                                                    ? "Disconnected"
                                                    : stage!.name.toString(),
                                                style: const TextStyle(
                                                    fontSize: 12,
                                                    fontWeight: FontWeight.bold,
                                                    color: Colors.red)),
                                          ],
                                        ),
                                      ),
                                    ],
                                  )
                                : Container(),
                            const SizedBox(
                              height: 12,
                            ),
                            Center(
                              child: GestureDetector(
                                onTap: () {
                                  if (controller.haveVpn == true) {
                                    if (_granted == null) {
                                      engine
                                          .requestPermissionAndroid()
                                          .then((value) {
                                        setState(() {
                                          _granted = value;
                                        });
                                      });
                                    }
                                    if (stage.toString() ==
                                            VPNStage.disconnected.toString() ||
                                        stage.toString() == "null") {
                                      initPlatformState(vpn: controller.vpn!);
                                    } else {
                                      Get.defaultDialog(
                                        titlePadding: const EdgeInsets.only(
                                            top: 10, bottom: 10),
                                        contentPadding: const EdgeInsets.only(
                                            top: 10,
                                            bottom: 10,
                                            right: 15,
                                            left: 15),
                                        title: "Warning:",
                                        middleText:
                                            "The connection will be disconnected.",
                                        middleTextStyle: const TextStyle(
                                            fontWeight: FontWeight.w600,
                                            fontSize: 16),
                                        textConfirm: "Confirm",
                                        textCancel: "Cancel",
                                        radius: 8,
                                        onConfirm: () {
                                          engine.disconnect();
                                          Get.back();
                                        },
                                        onCancel: () {
                                          //
                                          Get.back();
                                        },
                                        buttonColor: Colors.red,
                                      );
                                    }
                                  } else {
                                    Get.toNamed(VPNRoute.serverlist);
                                  }
                                },
                                child: Container(
                                  height: 45,
                                  width: 150,
                                  padding: const EdgeInsets.all(5),
                                  margin: const EdgeInsets.all(5),
                                  decoration: BoxDecoration(
                                    borderRadius: BorderRadius.circular(10),
                                    color: (stage.toString() ==
                                                VPNStage.disconnected
                                                    .toString() ||
                                            stage.toString() == "null")
                                        ? Colors.grey.shade400
                                        : (stage.toString() ==
                                                VPNStage.connected.toString())
                                            ? Colors.green.shade400
                                            : Colors.green.shade200,
                                  ),
                                  child: Row(
                                    mainAxisAlignment: MainAxisAlignment.center,
                                    children: [
                                      Expanded(
                                        child: Text(
                                          (stage.toString() ==
                                                      VPNStage.disconnected
                                                          .toString() ||
                                                  stage.toString() == "null")
                                              ? "Connect Now"
                                              : (stage.toString() ==
                                                      VPNStage.connected
                                                          .toString())
                                                  ? "Connected"
                                                  : (stage.toString() ==
                                                          VPNStage
                                                              .wait_connection
                                                              .toString())
                                                      ? "Wating..."
                                                      : (stage.toString() ==
                                                              VPNStage
                                                                  .vpn_generate_config
                                                                  .toString())
                                                          ? "Generate VPN"
                                                          : "Wating...",
                                          maxLines: 1,
                                          textAlign: TextAlign.center,
                                          style: const TextStyle(
                                              color: Colors.white,
                                              fontSize: 16,
                                              fontWeight: FontWeight.w500),
                                        ),
                                      ),
                                      const SizedBox(
                                        width: 5,
                                      ),
                                      Icon(
                                        Icons.power_settings_new,
                                        size: 23,
                                        color: (stage.toString() ==
                                                    VPNStage.disconnected
                                                        .toString() ||
                                                stage.toString() == "null")
                                            ? Colors.grey.shade800
                                            : (stage.toString() ==
                                                    VPNStage.connected
                                                        .toString())
                                                ? Colors.green.shade800
                                                : Colors.white,
                                      ),
                                      const SizedBox(
                                        width: 5,
                                      ),
                                    ],
                                  ),
                                ),
                              ),
                            ),
                            const SizedBox(
                              height: 5,
                            ),
                          ],
                        ),
                      ),
                    ],
                  ),
                ),
              );
            }),
        GestureDetector(
          onTap: () {
            if (stage.toString() == VPNStage.disconnected.toString() ||
                stage.toString() == "null") {
              Get.toNamed(VPNRoute.serverlist);
            } else {
              Get.defaultDialog(
                titlePadding: const EdgeInsets.only(top: 10, bottom: 10),
                contentPadding: const EdgeInsets.only(
                    top: 10, bottom: 10, right: 15, left: 15),
                title: "Warning:",
                middleText: "The connection will be disconnected.",
                middleTextStyle:
                    const TextStyle(fontWeight: FontWeight.w600, fontSize: 16),
                textConfirm: "Confirm",
                textCancel: "Cancel",
                radius: 8,
                onConfirm: () {
                  engine.disconnect();
                  Get.back();
                },
                onCancel: () {
                  //
                  Get.back();
                },
                buttonColor: Colors.red,
              );
            }
          },
          child: Card(
            child: SizedBox(
              height: 45,
              child: Stack(
                children: [
                  Align(
                    alignment: Alignment.center,
                    child: Row(
                      mainAxisAlignment: MainAxisAlignment.center,
                      children: const [
                        Icon(Icons.location_on),
                        SizedBox(
                          width: 10,
                        ),
                        Text(
                          "Pick Your Server",
                          style: TextStyle(fontWeight: FontWeight.w600),
                        ),
                      ],
                    ),
                  ),
                  const Align(
                      alignment: Alignment.centerRight,
                      child: Padding(
                        padding: EdgeInsets.all(8.0),
                        child: Icon(Icons.chevron_right),
                      )),
                ],
              ),
            ),
          ),
        ),
      ]),
    );
  }
}
